import "./App.css";
import axios from "axios";
import React, { useEffect, useState } from "react";
import CreateProduct from "./Pages/CreateProduct";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";

function App() {
  const [productdeet, setDeets] = useState([]);
  const [selectedval, setSelectedval] = useState();
  useEffect(() => {
    axios.get("http://localhost:3001/products").then((response) => {
      setDeets(response.data);
    });
  }, []);
  return (
    <>
      <div className="App">
        <select
          onChange={(e) => {
            const c = productdeet.find((x) => x.id === e.target.value);
            setSelectedval(c);
          }}
        >
          <option value=" ">Select a product</option>
          {productdeet.map((value, key) => {
            return (
              <option value={value.id} key={value.id}>
                {value.title}
              </option>
            );
          })}
        </select>
        <input type="label">{selectedval}</input>
      </div>
      <div>
        <Router>
          <Routes>
            <Link to="/creatingProduct">Create a New Product</Link>
            <Route path="/creatingProduct" element=""></Route>
          </Routes>
        </Router>
      </div>
    </>
  );
}

export default App;
