import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

function EditingCus() {
  let { id } = useParams();
  const [customerDeets, setDeets] = useState([]);
  const [customerDeets1, setDeets1] = useState([]);
  useEffect(() => {
    axios.get(`http://localhost:3001/clients/byId/${id}`).then((response) => {
      setDeets(response.data);
    });
  });
  setDeets1(customerDeets1);
  return (
    <div>
      <textarea
        value={customerDeets1.cname}
        onChange={(e) => setDeets1(e.target.value)}
      ></textarea>
      <textarea value={customerDeets1.cphone}></textarea>
      <textarea value={customerDeets1.cemail}></textarea>
      <textarea value={customerDeets1.caddress1}></textarea>
      <textarea value={customerDeets1.caddress2}></textarea>
      <textarea value={customerDeets1.caddress3}></textarea>
      <textarea value={customerDeets1.cpincode}></textarea>
    </div>
  );
}

export default EditingCus;
