import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

function EditingCus() {
  let { id } = useParams();
  const [customerDeets, setDeets] = useState([]);
  useEffect(() => {
    axios.get(`http://localhost:3001/clients/byId/${id}`).then((response) => {
      setDeets(response.data);
    });
  });
  const [cusName, setCusName] = useState({
    cname: customerDeets.cname,
  });
  return (
    <div>
      <input type="text" value={cusName.cname}></input>
      <input type="text" value={customerDeets.cphone}></input>
      <input type="text" value={customerDeets.cemail}></input>
      <input type="text" value={customerDeets.caddress1}></input>
      <input type="text" value={customerDeets.caddress2}></input>
      <input type="text" value={customerDeets.caddress3}></input>
      <input type="text" value={customerDeets.cpincode}></input>
    </div>
  );
}

export default EditingCus;
