import React from "react";
import { Formik, Form, Field, ErrorMessage } from "formik";
import * as Yup from "yup";

function CreateProduct() {
  const initial = {
    title: "",
    price: "",
    description: "",
  };
  const validatingdata = Yup.object().shape({
    title: Yup.string().required(),
    price: Yup.number(),
    description: Yup.string(),
  });

  const submitted = (data) => {
    console.log(data);
  };
  return (
    <div>
      <Formik initialValues={initial} onSubmit={submitted}>
        <Form>
          <label>Please enter Product Name to the the field</label>
          <Field name="title" placeholder="Enter the Product Name"></Field>
          <br></br>
          <label>Please enter Product Price to the the field</label>
          <Field name="price" placeholder="Enter the Product Price"></Field>
          <br></br>
          <label>Please enter Product Description to the the field</label>
          <Field
            name="description"
            placeholder="Enter the Product Description"
          ></Field>
          <br></br>
          <button type="submit">Save Product</button>
        </Form>
      </Formik>
    </div>
  );
}

export default CreateProduct;
