import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { Formik, Form, Field } from "formik";

function EditingCus() {
  let { id } = useParams();
  const [initialValues, setInitialValues] = useState({
    cname: "",
    cphone: "",
    cemail: "",
    caddress1: "",
    caddress2: "",
    caddress3: "",
    cpincode: "",
  });

  useEffect(() => {
    axios.get(`http://localhost:3001/clients/byId/${id}`).then((response) => {
      setInitialValues(response.data);
    });
  }, [id]);

  const handleSubmit = (values) => {
    // Add your save logic here, e.g., sending a PUT request to update the customer details
    console.log("Saved customer details:", values);
    // Example: axios.put(`http://localhost:3001/clients/${id}`, values);
  };

  return (
    <Formik
      enableReinitialize
      initialValues={initialValues}
      onSubmit={handleSubmit}
    >
      {({ values, handleChange }) => (
        <Form>
          <div>
            <Field
              as="textarea"
              name="cname"
              value={values.cname}
              onChange={handleChange}
            />
            <br />
            <br />
            <br />
            <Field
              as="textarea"
              name="cphone"
              value={values.cphone}
              onChange={handleChange}
            />
            <br />
            <br />
            <Field
              as="textarea"
              name="cemail"
              value={values.cemail}
              onChange={handleChange}
            />
            <br />
            <br />
            <Field
              as="textarea"
              name="caddress1"
              value={values.caddress1}
              onChange={handleChange}
            />
            <br />
            <br />
            <br />
            <Field
              as="textarea"
              name="caddress2"
              value={values.caddress2}
              onChange={handleChange}
            />
            <br />
            <br />
            <Field
              as="textarea"
              name="caddress3"
              value={values.caddress3}
              onChange={handleChange}
            />
            <br />
            <br />
            <br />
            <Field
              as="textarea"
              name="cpincode"
              value={values.cpincode}
              onChange={handleChange}
            />
            <br />
            <br />
            <br />
            <button type="submit">Save Edited Details</button>
          </div>
        </Form>
      )}
    </Formik>
  );
}

export default EditingCus;
