import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { Formik, Form, Field } from "formik";

function EditingCus() {
  let { id } = useParams();
  const [initialValues, setInitialValues] = useState({
    cname: "",
    cphone: "",
    cemail: "",
    caddress1: "",
    caddress2: "",
    caddress3: "",
    cpincode: "",
  });

  useEffect(() => {
    axios.get(`http://localhost:3001/clients/byId/${id}`).then((response) => {
      setInitialValues(response.data);
    });
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCustomerDeets((prevDetails) => ({
      ...prevDetails,
      [name]: value,
    }));
  };

  const handleSubmit = (values) => {
    // Add your save logic here, e.g., sending a PUT request to update the customer details
    console.log("Saved customer details:", values);
    // Example: axios.put(`http://localhost:3001/clients/${id}`, values);
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <textarea
          name="cname"
          value={customerDeets.cname}
          onChange={handleChange}
        ></textarea>
        <br />
        <br />
        <br />
        <textarea
          name="cphone"
          value={customerDeets.cphone}
          onChange={handleChange}
        ></textarea>
        <br />
        <br />
        <textarea
          name="cemail"
          value={customerDeets.cemail}
          onChange={handleChange}
        ></textarea>
        <br />
        <br />
        <textarea
          name="caddress1"
          value={customerDeets.caddress1}
          onChange={handleChange}
        ></textarea>
        <br />
        <br />
        <br />
        <textarea
          name="caddress2"
          value={customerDeets.caddress2}
          onChange={handleChange}
        ></textarea>
        <br />
        <br />
        <textarea
          name="caddress3"
          value={customerDeets.caddress3}
          onChange={handleChange}
        ></textarea>
        <br />
        <br />
        <br />
        <textarea
          name="cpincode"
          value={customerDeets.cpincode}
          onChange={handleChange}
        ></textarea>
        <br />
        <br />
        <br />
        <button type="submit">Save Edited Details</button>
      </div>
    </form>
  );
}

export default EditingCus;
