import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

function EditingCus() {
  let { id } = useParams();
  const [customerDeets, setDeets] = useState([]);
  useEffect(() => {
    axios.get(`http://localhost:3001/clients/byId/${id}`).then((response) => {
      setDeets(response.data);
    });
  });

  return (
    <>
      <div>
        <input type="text" value={customerDeets.cname}></input>
        <br></br>
        <br></br>
        <br></br>
        <textarea Text={customerDeets.cphone}></textarea>
        <br></br>
        <br></br>
        <textarea Text={customerDeets.cemail}></textarea>
        <br></br>
        <br></br>
        <textarea Text={customerDeets.caddress1}></textarea>
        <br></br>
        <br></br>
        <br></br>
        <textarea Text={customerDeets.caddress2}></textarea>
        <br></br>
        <br></br>
        <textarea Text={customerDeets.caddress3}></textarea>
        <br></br>
        <br></br>
        <br></br>
        <textarea Text={customerDeets.cpincode}></textarea>
        <br></br>
        <br></br>
        <br></br>
        <button type="submit">Save Edited Details</button>
      </div>
    </>
  );
}

export default EditingCus;
